mapper.select:
select

from SERVICE_ORDER
where 1=1

	and ID = #{id}


	and ORDER_NO = #{orderNo}


	and BANK_CODE = #{bankCode}


	and ORG_NO = #{orgNo}


	and BANK_ORDER_NO = #{bankOrderNo}


	and SUPPLY_ORDER_NO = #{supplyOrderNo}


	and SUPPLY_NO = #{supplyNo}


	and PRODUCT_NO = #{productNo}


	and SETTLE_DATE = #{settleDate}


	and ORDER_STATUS = #{orderStatus}


	and SUB_ORDER_NO = #{subOrderNo}

limit 1

        select  from SERVICE_ORDER
        where 1=1

				and ID = #{id}


				and ORDER_NO = #{orderNo}


				and BANK_CODE = #{bankCode}


				and ORG_NO = #{orgNo}


				and BANK_ORDER_NO = #{bankOrderNo}


				and SUPPLY_ORDER_NO = #{supplyOrderNo}


				and SUPPLY_NO = #{supplyNo}


				and PRODUCT_NO = #{productNo}


				and SETTLE_DATE = #{settleDate}


				and ORDER_STATUS = #{orderStatus}


				and SUB_ORDER_NO = #{subOrderNo}
